import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { OptForm } from '../components';
import '../assets/nav.css';

const Nav = () => {
  const [show, setShow] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 100) {
        setShow(true);
      } else {
        setShow(false);
      }
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className={`nav ${show && 'nav_black'}`}>
      <div className="nav-container">
        <a href="/" className="link">TrailerPeak</a>

        <div className="nav-links">
          <a href="/series" className="link">Tv Shows</a>
          <a href="/movies" className="link">Movies</a>
        </div>

        <div className="nav-search">
          <OptForm>
            <OptForm.Input placeholder="Search" />
            <OptForm.Button>üîç</OptForm.Button>
          </OptForm>
        </div>

        <img
          className="nav_avatar"
          src="https://res.cloudinary.com/doammcpie/image/upload/v1618310958/2_vohnwl.png"
          alt="naruto logo"
        />
      </div>
    </div>
  );
};

export default Nav;
