import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { OptForm } from '../components';
import '../assets/nav.css';

const API_KEY = 'YOUR_TMDB_API_KEY';  // Replace with actual API key

const Nav = () => {
  const [show, setShow] = useState(false);
  const [query, setQuery] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const handleScroll = () => {
      setShow(window.scrollY > 100);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!query.trim()) return;

    try {
      const response = await fetch(
        `https://api.themoviedb.org/3/search/keyword?api_key=${API_KEY}&query=${query}`
      );
      const data = await response.json();

      navigate('/search', { state: { results: data.results, query } });
    } catch (error) {
      console.error('Error fetching search results:', error);
    }
  };

  return (
    <div className={`nav ${show && 'nav_black'}`}>
      <div className="nav-container">
        <a href="/" className="link">TrailerPeak</a>

        <div className="nav-links">
          <a href="/series" className="link">Tv Shows</a>
          <a href="/movies" className="link">Movies</a>
        </div>

        <OptForm className="nav-search" onSubmit={handleSearch}>
          <OptForm.Input
            placeholder="Search..."
            value={query}
            onChange={(e) => setQuery(e.target.value)}
          />
          <OptForm.Button />
        </OptForm>

        <img
          className="nav_avatar"
          src="https://res.cloudinary.com/doammcpie/image/upload/v1618310958/2_vohnwl.png"
          alt="naruto logo"
        />
      </div>
    </div>
  );
};

export default Nav;
